INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce6c89015bce6e7e620000', '西游记', '99', '49', '02', , '《西游记》是中国古代第一部浪漫主义章回体长篇神魔小说。', '该书以“唐僧取经”这一历史事件为蓝本，通过作者的艺术加工，深刻地描绘了当时的社会现实。主要描写了孙悟空出世，后遇见了唐僧、猪八戒和沙和尚三人，一路降妖伏魔，保护唐僧西行取经，经历了九九八十一难，终于到达西天见到如来佛祖，最终五圣成真的故事。', '79');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce7793450000', '幸存者游戏', '59', '20', '02', , '时光回到一九三九年至一九四八年间。一家人围坐在老式拱形收音机旁，收听《埃勒里·奎因探案记》。伴随着按键咔的一声和调频的嘶嘶声，父亲或母亲打开了收音机。', '随即，声音浑厚的播音员（伯特·帕克斯或欧内斯特·查普尔或另外一个男人）邀请大家聆听大名鼎鼎的侦探口述他如何独自破案的过程，并鼓励观众发挥聪明才智，与其一决高下……', '49');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce77ebb40001', '古文观止', '40', '20', '02', , '《古文观止》是清人吴楚材、吴调侯于康熙三十三年（1694年）选定的古代散文选本。', '《古文观止》300年来流传极广、影响极大，在诸多古文选本中独树一帜，鲁迅先生评价《古文观止》时认为它和《昭明文选》一样，“在文学上的影响，两者都一样的不可轻视”。', '20');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce782a960002', '水浒传', '40', '42', '02', , '《水浒传》，是中国四大名著之一，全书描写北宋末年以宋江为首的108位好汉在梁山聚义，以及聚义之后接受招安、四处征战的故事。', '《水浒传》以农民起义的发生、发展过程为主线，通过各个英雄被逼上梁山的不同经历，描写出他们由个体觉醒到走上小规模联合反抗，到发展为盛大的农民起义队伍的全过程，表现了“官逼民反”这一封建时代农民起义的必然规律，塑造了农民起义领袖的群体形象，深刻反映出北宋末年的政治状况和社会矛盾。', '29');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce786f7d0003', '红楼梦', '50', '30', '02', , '《红楼梦》，中国古典四大名著之首，清代作家曹雪芹创作的章回体长篇小说又名《石头记》《金玉缘》。此书分为120回“程本”和80回“脂本”两种版本系统。', '《红楼梦》以“大旨谈情，实录其事”自勉，只按自己的事体情理，按迹循踪，摆脱旧套，新鲜别致，取得了非凡的艺术成就。“真事隐去，假语村言”的特殊笔法更是令后世读者脑洞大开，揣测之说久而遂多  。围绕《红楼梦》的品读研究形成了一门显学——红学', '40');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce78b23a0004', '科学虫子', '35', '199', '01', , '这本书由科学家和儿童文学家携手创作，以小说的形式，记录了一个教授为5个小孩子讲授的5堂天文课。这5堂课，由一位作者负责故事、剧情，另一位作者负责讲解专业的天文学，使得每一堂课都兼具趣味性、文学性和知识性。', '所涉及的天文学知识，包括地球的诞生、水如何来到地球、月亮的故事、行星的故事、恒星的故事五个方面。丰富的天文学知识通过教授和五个小屁孩之间有趣的对话，慢慢展开。这位大教授即是作者之一的物理学教授哈德•莱士，他用非常浅显易懂的语言和生动形象的比喻，将看似高深莫测的天文学讲解得非常通透。而且在讲解知识点之外，还穿插着5个孩子之间，以及和教授之间的感情，这份细腻而深厚的感情在剧作家梅思的生花妙笔之下，和宇宙观巧妙地融合在一起，令小读者们更深入地思考自己和宇宙、和他人的关系。', '35');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce78fd1a0005', '三国演义', '50', '39', '03', , '《三国演义》是中国古典四大名著之一，是中国第一部长篇章回体历史演义小说,全名为《三国志通俗演义》（又称《三国志演义》），作者是元末明初的著名小说家罗贯中。', '《三国演义》描写了从东汉末年到西晋初年之间近105年的历史风云，以描写战争为主，诉说了东汉末年的群雄割据混战和汉、魏、吴三国之间的政治和军事斗争，最终司马炎一统三国，建立晋朝的故事。反映了三国时代各类社会斗争与矛盾的转化，并概括了这一时代的历史巨变，塑造了一批叱咤风云的三国英雄人物。全书可大致分为黄巾之乱、董卓之乱、群雄逐鹿、三国鼎立、三国归晋五大部分。在广阔的背景上，上演了一幕幕气势磅礴的战争场面。编者罗贯中将兵法三十六计融于字里行间，既有情节，也有兵法韬略。', '50');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce797d5e0006', '思想的星空', '20', '149', '01', , '《思想的星空》为周国平散文经典之一。', '《思想的星空》内容为：我原是一个易感的人，容易为情所困，跳不出来。我又是一个天性悲观的人，从小就想死亡的问题，容易看破红尘。因此，我面临双重的危险：既可能毁于色，也可能堕入空。我的一生实际上都是在与这两种危险作斗争，在色与空之间寻找一个安全的中间地带。', '20');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce79b93c0007', '肖生克的救赎', '49', '100', '01', , '《肖申克的救赎》是美国作家斯蒂芬·金的中篇小说，也是其代表作。收录于小说合集《四季奇谭》中，副标题为“春天的希望”。', '该作讲述银行家安迪，被当作杀害妻子的凶手送上法庭。妻子的不忠、律师的奸诈、法官的误判、狱警的凶暴、典狱长的贪心与卑鄙，将正处在而立之年的安迪一下子从人生的巅峰推向了世间地狱。而狱中发生的一系列事情迫使忍无可忍的安迪终于在一个雷电交加的夜晚，越狱而出，重获自由。当翌日典狱长打开安迪的牢门时，发现他已不翼而飞，预感到末日来临的典狱长在检察人员收到安迪投寄的罪证之前，畏罪自杀。', '49');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815bce71c5015bce7a455b0008', '仰望幸福', '30', '50', '01', , '心灵康复之路，并非始于向外盲目的跳跃，而始于向内心温和的回归。', '很多时候，你在生活中的失败并不是因为你能力低下，力量薄弱，而是你信心不足，还没有上场，就败下阵来。这个世界是由自信心创造出来的，充分的自信是取得成功的重要条件，读读这本书，它能帮你确立积极乐观的心态，能让你战胜生活中的恐惧。', '30');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c09e17d015c0b25c54e0001', '华尔街之战', '122', '40', '01', , '《华尔街之战》与其说是自传，不如说是一本精彩的小说。', '上世纪90年代初，31岁的贝尔福特已坐拥亿万家财，这位投资天才创立的投资公司曾创造过3分钟内进账1200万美元的奇迹，但其生钱的手段却不怎么光彩，打擦边球之余甚至还触犯法律。其奢靡是生活方式更是令人咋舌，交易室里大开派对，让一艘170英尺长的动力游艇沉落大海，在酒店一次性消费70万美元，酗酒、吸毒、嫖妓等等。纸醉金迷的生活在1998年戛然而止，那年他因诈骗投资者2亿美元而被判入狱22个月。', '122');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c09e17d015c0b27946d0002', '空之境界', '30', '50', '01', , '《空之境界》是日本作家奈须蘑菇所写的奇幻小说，由武内崇完成插画、人设部分，副标题为《the Garden of sinners》。', '该作品讲述拥有着直死之魔眼的少女两仪式与许多魔术师之间的发种种纠葛事件。描绘了人性，起源，感情，矛盾，肉体与灵魂等等深刻的主题。空之境界也是型月世界观的完善与升华，主要讲述的是型月世界的根源问题。', '30');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c09e17d015c0b27cea80003', '暗战', '44', '49', '03', , '《暗战》情节跌宕起伏、扣人心弦,是一本情节与文笔俱佳的玄幻小说', '　衣香鬓影、觥筹交错的上流商圈酒会上，神秘美丽的何楚挟着复仇之剑出现在青年才俊许湛面前。两人几个回合的交锋，何楚成功地引起了他对她的兴趣。而许湛面对何楚的进攻并没有防守，而选择了征服她。', '44');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c09e17d015c0b280ec30004', '火的女儿', '55', '100', '03', , '《火的女儿》是法国大诗人奈瓦尔的小说集，包括他最著名的小说如《安婕丽嘉》《西尔薇娅》《奥蕾莉娅》等，这些小说对法国文学产生了深远的影响，普鲁斯特的《追忆似水年华》、阿兰-傅尼埃的《大莫纳》、安德烈·布勒东的《娜佳》都从奈瓦尔那里受到启发。', '在钱拉·德·奈瓦尔身上，与生俱来的疯癫在未发作时，仅仅表现为过分主观，可以说比大家更加重视一场梦幻、一丝回忆，更加重视感觉的个人质地；对于现实，大家的感觉和觉察相同，他却更为注重。这种艺术秉性，用福楼拜的话来讲，导致把现实仅“用来当幻觉描绘”的天赋，导致造成假象的天赋，这原本用来描绘某种现实是很有价值的，最后却变成癫狂，正好切合文学独有的精华演变……', '55');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c102d8b015c107daaa6001e', '金翅雀', '50', '100', '02', , '我们所经历的一切，都将使我们更接近真实与美。', '纽约大都会博物馆发生爆炸，男孩西奥的母亲丧命，十三岁的西奥奇迹般幸存。但由于父亲早已遗弃他们母子，西奥只能寄住在有钱的同学家。陌生的环境令他不知所措，崭新的人际关系令他倍感挫折，但最令他难以忍受的是失去母亲的伤痛。 　　但他意外拥有了博物馆的名画《金翅雀》。这幅画是他在回忆起母亲时的唯一慰藉，将他带进了幽深黑暗的艺术世界……', '45');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c102d8b015c107f07aa0020', '映空：银河十二夜', '40', '30', '02', , '“映空”系列第三辑作品，主题是“银河十二夜”。', '让现实安静下来，让克制和隐忍消失，让天空清澈辽远，让气温不热不冷，让寂寞随意展现，让交流自由洒脱，让努力发自真心，让幸福水到渠成。让时间潺潺而流，让奔腾不息的声音不再烦扰我耳边。', '30');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c102d8b015c107f07ab0021', '黑暗的心', '30', '20', '02', , '《黑暗的心》（1902）是英籍波兰作家约瑟夫·康拉德的主要作品之一。被认为是英国文学史上第一部真正意义上的现代主义小说，在这部作品中作者对人类文明以及人性这一主题作了深刻的思考。', '《黑暗的心》记录了船长马洛在一艘停靠于伦敦外的海船上所讲的刚果河的故事。马洛的故事除了涉及马洛自己年轻时的非洲经历之外，主要讲述了他在非洲期间所认识的一个叫库尔兹的白人殖民者的故事——一个矢志将“文明进步”带到非洲的理想主义者，后来堕落成贪婪的殖民者的经过。', '23');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c107f30015c10824e750000', '放牛班大改造', '39', '10', '02', , '师生互相理解的金钥匙，感动又振奋的校园励志歌。', '一个来自都市的失意老师，一群淘气的乡村学生，从一开始的陌生疏离，到后来建立起深厚的情谊，他把学生当成自己的孩子一般无私付出，用关心代替责罚，用宽容代替偏见，倾听学生，灌输自信，让这个放牛班充满了信任与温暖……', '29');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c110e80015c112ddabd0001', '孙传芳幕府与幕僚', '122', '20', '01', , '介绍曾称霸东南一时的孙传芳及其核心幕僚的作为与命运，随文配有几十张珍贵历史照片', '孙传芳之所以能够称霸一时，与他善于笼络人才有很大关系。他的幕府里不仅有赳赳武夫，还有不少专门人才和文人学士，如蒋百里、陈陶遗、丁文江、陈仪、章太炎等。江南的社会名流都对他有好感。', '122');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c295c3b015c29b554800003', '战争史笔记', '100', '39', '03', , '朱增泉将军以职业军人的专业目光审读中国古代战事的《战争史笔记 (元-明)》，呈现金戈铁马的经典战役，深度评说兴衰存亡中的王朝更迭。', '《战争史笔记》是作者的一部散文化的战争史笔记，或曰一部笔记体的战争史。凡历史，读起来都是比较枯燥和沉闷的，战争史也不例外。这是作者的一种尝试，力求把战争史写得通俗易懂，引人入胜。《元-明》详细叙述元、明、清三个朝代的战争历史。', '100');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c681ad2015c684741590006', '我爱故我在', '22', '100', '01', , '暌违五年，陶晶莹最真实、最成熟、最痛快独白的纪念作读完它，你会变得很弱，也会变得更强。', '  爱情是什么？爱能永远吗？爱如果变质了，又或者，它一定会变质，该怎么办？     你可以鱼，人类的文明、进步、改变，是因为生活的需要而产生，但仔细想想，人类的一切需要，都是因为爱和被爱而衍生的。     所以，爱最大。', '22');
INSERT INTO bookshop.t_book (BOOK_ID, BOOK_NAME, BOOK_PRICE, BOOK_NUM, BOOK_ACT, BOOK_IMG, BOOK_DESC, BOOK_MORE_DESC, BOOK_SPE_PRICE) VALUES ('40289b815c681ad2015c6850af870007', '来去乡下过日子', '99', '40', '01', , '日本首位环境保育者的亲垦记录 与京都山居生活齐名的生态生活指南', '一直从事NPO工作并开创自己的环境教育事业“地球意识”的加藤大吾，在女儿出生后终于下定决心，从喧闹繁忙的东京搬到山梨县都留市的山间生活。 　　一切都是从零开始。他把无法直接使用的斜坡地规整为平地，自画图纸，借鉴老实房屋构造，利用拆屋废材，建起了可充分利用风能和太阳能、供一家人舒适居住的木屋。向当地农民请教，挑战合鸭农法，进行生态耕种，实现稻米、蔬菜的自给自足。自制味增、晾晒柿饼、制作梅干、烹制草茶，利用自己的双手，充分享受大自然的馈赠。', '99');